import React, {Component} from 'react';
import Radium from 'radium';
import githubIcon from '../../Static/github-brands.svg'

const appear = Radium.keyframes({
  '0%': {opacity: 0,},
  '100%': {opacity: 1},
}, 'pulse');

const styles = {
  main:{
    position: 'absolute',
    height: '20vh',
    width: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#272727',
    animationName: appear,
    animationDuration: '1s',
    animationDelay: '2s',
    animationFillMode: 'forwards',
  },
  wrapper:{
    display: 'flex',
    height: 80,
    width: '80%',
    alignItems: 'center',
  },
  p:{
    color: '#fff',
    fontFamily: 'Poppins',
    margin: '0 5px 0 0'
  },
  iconWrapper: {
    position: 'absolute',
    left: '50%',
    transform: 'translateX(-50%)',
  },
  githubIcon: {
    width: 25,
  },
  backToTop: {
    position: 'absolute',
    bottom: 15,
    left: '50%',
    transform: 'translateX(-50%)',
  }
};

class Footer extends Component {
  render() {
    const {show} = this.props
    return (
      <div style={{
        ...styles.main,
        ...{opacity: (show ? 1: 0)}
        }}>
        <div style={styles.wrapper}>
          <p style={styles.p}>CREATED BY JAY CHOU</p>
          <p style={styles.p}>Â©2019</p>
          <a href="#top" style={styles.backToTop}>back to top</a>
          <div style={styles.iconWrapper}>
            <img style={styles.githubIcon} src={githubIcon} alt="github"/>
          </div>
        </div>
      </div>

    )
  }
}


export default Radium(Footer)
