import React, {Component} from 'react';
import Radium from 'radium';
import './Collection.css';
import NTUvideo from '../../Static/video/Highlight｜20180405 24th NTU Dance Competition 決賽.mp4';

const appear = Radium.keyframes({
  '0%': {opacity: 0},
  '100%': {opacity: 1},
}, 'pulse');


const styles = {
  wrapper:{
    height: '100%',
    width: '100%',
    display: 'flex',
    flexDirection: 'column',
    padding:' 200px 0 0 0',
    animationName: appear,
  },
  h1:{
    animationName: appear,
    animationDuration: '1s',
    animationFillMode: 'forwards',
  },
  videoWrapper: {
    width: '100%',
    height: '100%',
    // animationName: appear,
    // animationDuration: '5s',
  },
  testVideo: {
    position: 'fixed',
    minWidth: '100%',
    minHeight: '100%',
    margin: 0,
    padding: 0,
    left: 0,
    bottom: 0,
    zIndex: -1,
    filter: 'grayscale(80%)',
    opacity: 0,
  },
  timelineHeader: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'initial',
    alignItems: 'center',
    margin: '0 0 80px 0',
    animationName: appear,
  },
  timeline: {
    display: 'flex',
    margin: '0 auto',
    flexWrap: 'wrap',
    flexDirection: 'column',
    maxWidth: 700,
    position: 'relative',
    animationName: appear,
  },
  line: {
    position: 'absolute',
    left: '50%',
    width: 2,
    height: '100%',
    marginLeft: -1,
    backgroundColor: 'black', //shoudchange
    animationName: appear,
  },
  timelineItem: {
    padding: '40px 0',
    opacity: '.3',
    filter: 'blur(2px)',
    transition: '.5s',
    boxSizing: 'border-box',
    width: 'calc(50% - 40px)',
    display: 'flex',
    position: 'relative',
    transform: 'translateY(-80px)',
    animationName: appear,
  },
  timelineImg: {
    maxWidth: '100%',
    boxShadow: '0 10px 15px rgba(0, 0, 0, 0.4)',
    animationName: appear,
  },
  timelineTitle: {
    letterSpacing: 3,
    width: '100%',
    position: 'absolute',
    color: 'black', //should change rgba(255, 255, 255, 0.5)
    fontSize: 13,
    fontFamily: '"Pathway Gothic One", sans-serif',
    borderLeft: '2px solid rgba(255, 255, 255, 0.5)',
    top: '70%',
    margin: '-5px 0 0 0',
    padding: '0 0 0 15px',
    opacity: 0,
    right: 'calc(-100% - 56px)',
    animationName: appear,
  },
  timelineTitle__active: {
    top: '50%',
    transition: '.3s all .2s',
    opacity: 1,
  },
  timelineContentTitle: {
    fontSize: 66,
    margin: '-10px 0 0 0',
    transition: '.4s',
    padding: '0 10px',
    boxSizing: 'border-box',
    fontFamily: '"Pathway Gothic One", sans-serif',
    color: '#fff',
    animationName: appear,
  },
  timelineItem__active: {
    opacity: 1,
    transform: 'translateY(0)',
    filter: 'blur(0px)',
  },
  youtubePlayer: {
    position: 'absolute',
    zIndex: 0,
    width: '100%',
    height: '100%',
    top: 0,
    left: 0,
    overflow: 'hidden',
    opacity: 1,
    userSelect: 'none',
    marginTop: 0,
    marginLeft: -333,
    maxWidth: 'initial',
    transitionProperty: 'opacity',
    transitionDuration: '1000ms',
  }
};


class Collection extends Component {
  constructor(props){
    super(props)
    this.state = {
      opacity: false,
    }
  }

  componentDidMount(){
    this.node.scrollIntoView();
    setTimeout(() => {
      this.setState({
        opacity:true
      })
    },2000)
  }

  render() {
    const { handleScroll } = this.props;
    const { opacity } = this.state;
    console.log(handleScroll);
    return (
      <>
        <div ref={node => this.node = node} />
        <div style={styles.wrapper}>
          {/* <video autoPlay muted loop style={styles.testVideo}>
            <source src={NTUvideo} type="video/mp4" />
          </video> */}
          <div style={styles.videoWrapper}>
            <iframe
              style={{
                ...styles.testVideo,
                ...opacity ? {opacity:0.1} : null}}
              width="560"
              height="315"
              src="https://www.youtube.com/embed/kfzBM6ot9GI?controls=0&showinfo=0&rel=0&autoplay=1&loop=1&modestbranding=0"
              frameBorder="0"
              allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
              title="Youtube Player"
              unselectable="on" />
          </div>
          <div style={styles.timelineHeader}>
            <h2>經歷</h2>
            <h3>experience</h3>
          </div>
          <div style={styles.timeline}>
            <div style={styles.line} />
            <div style={{
              ...styles.timelineItem,
              ...styles.timelineItem__active}}>
              <div style={{
                ...styles.timelineTitle,
                ...styles.timelineTitle__active}}>NTU COMPETITION</div>
              <div style={styles.timelineContent}>
                <img style={styles.timelineImg} src="https://i.imgur.com/dnFtQI6.jpg" alt="ntuImg" />
                <h2 style={styles.timelineContentTitle}>2017.5</h2>
                <p style={styles.timelineContentDesc}>
                  全台公認最具指標性排舞競賽，被視為街舞年度盛會。將近2200名現場觀眾、直播線上人數可達一萬四千人。於2017年擔任總召，期間帶領50位團隊成員，策劃初賽＆決賽流程，並成功接洽Calvin Klein、SONY、YAHOO!等知名廠商合作
                </p>
              </div>
            </div>
            <div style={{
              ...styles.timelineItem,
              ...styles.timelineItem__active}}>
              <div style={{
                ...styles.timelineTitle,
                ...styles.timelineTitle__active}}>NTU COMPETITION</div>
              <div style={styles.timelineContent}>
                <img style={styles.timelineImg} src="https://i.imgur.com/dnFtQI6.jpg" alt="ntuImg" />
                <h2 style={styles.timelineContentTitle}>2017.5</h2>
                <p style={styles.timelineContentDesc}>
                  全台公認最具指標性排舞競賽，被視為街舞年度盛會。將近2200名現場觀眾、直播線上人數可達一萬四千人。於2017年擔任總召，期間帶領50位團隊成員，策劃初賽＆決賽流程，並成功接洽Calvin Klein、SONY、YAHOO!等知名廠商合作
                </p>
              </div>
            </div>
            <div style={{
              ...styles.timelineItem,
              ...styles.timelineItem__active}}>
              <div style={{
                ...styles.timelineTitle,
                ...styles.timelineTitle__active}}>NTU COMPETITION</div>
              <div style={styles.timelineContent}>
                <img style={styles.timelineImg} src="https://i.imgur.com/dnFtQI6.jpg" alt="ntuImg" />
                <h2 style={styles.timelineContentTitle}>2017.5</h2>
                <p style={styles.timelineContentDesc}>
                  全台公認最具指標性排舞競賽，被視為街舞年度盛會。將近2200名現場觀眾、直播線上人數可達一萬四千人。於2017年擔任總召，期間帶領50位團隊成員，策劃初賽＆決賽流程，並成功接洽Calvin Klein、SONY、YAHOO!等知名廠商合作
                </p>
              </div>
            </div>
          </div>
        </div>
      </>
    )
  }
}

export default Radium(Collection)
