import React, {Component} from 'react';
import MainFullStack from './MainFullStack';

import logo from './logo.svg';
import './Main.css';
import './MainHover.css'


// const skill = ['HTML5', 'CSS', 'Javascript', 'React', 'Redux', 'Radium', 'Vue', 'ESlint', ];
// const skillDisplay = skill[Math.floor(Math.random()*skill.length)]

const setTimeClass =  {
  "arrowLeft": "arrowHover",
  "hr": "hrHover",
  "hrturn": "hrturnHover",
  "arrowRight": "arrowHover",
}



class Main extends Component {
  constructor(props){
    super(props)
    this.state = {
      active: false
    }
  }

  componentDidMount(){
    this.node.scrollIntoView();
    setTimeout(() => {
      this.setState({
        active: true
      })
    },3000)
  }

  render() { 
    const {active} = this.state
    return (
      <div className="main">
        <div ref={node => this.node = node} />
        <div className="homepage">
          <header>
            <h1 className ={`name ${ active && 'nameHover'}`}
            >周杰</h1>
            {Object.keys(setTimeClass).map(val =>
              <hr className ={`val ${ val && setTimeClass[val]}`}/>
            )}
            {/* <hr className ={`arrowLeft ${ active && 'arrowHover'}`}/>
            <hr className ={`hr ${ active && 'hrHover'}`}/>
            <hr className ={`hrturn ${ active && 'hrturnHover'}`}/>
            <hr className ={`arrowRight ${ active && 'arrowHover'}`}/> */}
              {/* <img src={logo} className ={`Timing-logo ${active && 'timingLogoHover'}`} alt="Timing-logo"/>
            <a className ={`click ${active && 'clickHover'}`} href="#mainFullStack">click</a> */}
          </header>
        </div>
        <div className='seperate' />
        <MainFullStack />
        <section></section>
        <section></section>
      </div>  
    )
  }
}



export default Main
