import React, {Component} from 'react';
import Radium from 'radium';
import NTUvideo from '../../Static/video/Highlight｜20180405 24th NTU Dance Competition 決賽.mp4';

const appear = Radium.keyframes({
  '0%': {opacity: 0,},
  '100%': {opacity: 1},
}, 'pulse');

const styles = {
  wrapper:{
    height: '100vh',
    width: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  h1:{
    animationName: appear,
    animationDuration: '1s',
    animationFillMode: 'forwards',
  },
  testVideo: {
    position: 'fixed',
    minWidth: '100%',
    minHeight: '100%',
    margin: 0,
    padding: 0,
    left: 0,
    bottom: 0,
    zIndex: -1,
    filter: 'grayscale(80%)',
    opacity: 0.1,
  },
  timelineWrapper: {
    width: '100%',
    height: '100%',
    padding:' 200px 0 0 0',
  },
  timelineHeader: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'initial',
    alignItems: 'center',
    margin: '0 0 80px 0',
  },
  line: {
    position: 'absolute',
    left: '50%',
    width: 2,
    height: '100%',
    marginLeft: -1,
    backgroundColor: 'black',
  },
};


class Collection extends Component {

  componentDidMount(){
    this.node.scrollIntoView();
  }

  render() {
    return (
      <>
        <div ref={node => this.node = node} />
        <div style={styles.wrapper}>
          <div style={styles.timelineWrapper}>
            <div style={styles.timelineHeader}>
              <h2>作品集</h2>
              <h3>一些作品</h3>
            </div>
            <div style={styles.timeline}>
              <div style={styles.line} />
              <div class="timeline__content">
                <img class="timeline__img" src="http://i.cdn.ensonhaber.com/resimler/diger/ataturk_3473.jpg" alt="test" />
                <h2 class="timeline__content-title">1881</h2>
                <p class="timeline__content-desc">He was born in 1881 (probably in the spring) in Salonica, then an Ottoman city, now inGreece. His father Ali Riza, a customs official turned lumber merchant, died when Mustafawas still a boy. His mother Zubeyde, adevout and strong-willed woman, raised him and his sister.</p>
              </div>
            </div>
          </div>
        </div>
      </>
    )
  }
}

// <video autoPlay muted loop style={styles.testVideo}>
//   <source src={NTUvideo} type="video/mp4" />
// </video>
{/* <iframe src="https://player.vimeo.com/video/332939901" width="640" height="564" frameborder="0" allow="autoplay; fullscreen" allowfullscreen></iframe> */}


export default Radium(Collection)
