            <hr className ={`arrowRight ${ active && 'arrowHover'}`}/>
              <img src={logo} className ={`Timing-logo ${active && 'timingLogoHover'}`} alt="Timing-logo"/>
            <a id="click" href="#mainFullStack">click</a>
          </header>
        </div>
        <div className='seperate' />
        <MainFullStack />
        <section></section>
        <section></section>
      </div>  
    )
  }
}



export default Radium(Main)
