import React, {Component} from 'react';
import MainFullStack from './MainFullStack';
import Radium from 'radium';


import logo from './logo.svg';
import './Main.css';

// const skill = ['HTML5', 'CSS', 'Javascript', 'React', 'Redux', 'Radium', 'Vue', 'ESlint', ];
// const skillDisplay = skill[Math.floor(Math.random()*skill.length)]


const styles= {
  timingLogoHover: {
    right: '50%',
    opacity: 0,
  },
  nameHover:{
    left: '50%',
    opacity: 0,
  },
  hrHover: {
    transform: 'rotate(270deg)',
  },
  arrowHover: {
    top: 185,
    opacity: 1,
  },
  HrturnHover: {
    transform: 'rotate(-270deg)',
  },
}

let timeover = () => setTimeout(() => {
  this.setState({
    active:true
  })
},2000)


class Main extends Component {
  constructor(props){
    super(props)
    this.state = {
      active: false
    }
  }

  render() { 
    const {active} = this.state
    return (
      <div className="main">
        <div className="homepage">
          <header>
            <h1 id="name"
                onMouseOver = {timeover()}
                style={ active && styles.timingLogoHover}
            >周杰</h1>
            <hr id="arrowLeft"/>
            <hr/>
            <hr id="hrturn" href="#content"/>
            <hr id="arrowRight"/>
              <img src={logo} className ="Timing-logo" alt="Timing-logo"/>
            <a id="click" href="#mainFullStack">click</a>
          </header>
        </div>
        <div className='seperate' />
        <MainFullStack />
        <section></section>
        <section></section>
      </div>  
    )
  }
}



export default Radium(Main)
