import React, {Component} from 'react';
import Radium from 'radium'

const appear = Radium.keyframes({
  '0%': {opacity: 0,},
  '100%': {opacity: 1},
}, 'pulse');

const styles = {
  wrapper:{
    height: '100vh',
    width: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  h1:{
    animationName: appear,
    animationDuration: '1s',
    animationFillMode: 'forwards',
  },
  testVideo: {
    position: 'fixed',
    minWidth: '100%',
    minHeight: '100%',
    margin: 0,
    padding: 0,
    left: 0,
    bottom: 0,
    zIndex: -1,
    filter: 'grayscale(80%)',
  }
};


class Collection extends Component {
  constructor(props){
    super(props)
    this.state = {
      active: false
    }
  }


  componentDidMount(){
    this.node.scrollIntoView();
  }

  render() {
    const {active} = this.state
    return (
      <>
        <div ref={node => this.node = node} />
        <button onClick={() => this.setState({active: !active})}>play</button>
        <div style={styles.wrapper}>
          <video autoPlay={active} muted loop style={styles.testVideo}>
            <source src="https://player.vimeo.com/external/293373689.sd.mp4?s=b0ff18802c6816a88537abf8b8b0a54221df860e&profile_id=164&oauth2_token_id=57447761" type="video/mp4" />
          </video>
          {/* <h1 style={styles.h1}>這邊大guy會放一些作品 </h1> */}
        </div>
      </>
    )
  }
}


export default Radium(Collection)
