import React, {Component} from 'react';
import Radium from 'radium';
import Posts from './Posts';
import FullPost from './FullPost';

const appear = Radium.keyframes({
  '0%': {opacity: 0,},
  '100%': {opacity: 1},
}, 'pulse');

const fackPost = [
  {
    id: 1,
    topic: '前端好難',
    content: 'TT',
  },
  {
    id: 2,
    topic:'後端也好難',
    content: 'TAT',
  },
  {
    id: 3,
    topic: '學完redux發現有graphql',
    content: 'QAQ',
  },
]

const styles = {
  postsWrapper: {
    animationName: appear,
    animationDuration: '1s',
    animationFillMode: 'forwards',
    height: '500%',
    width: '100%',
    padding: '15% 0 0 0',
    display: 'flex',
    justifyContent: 'center',
    flexDirection: 'column',
  },
  h1:{
    animationName: appear,
    animationDuration: '1s',
    animationFillMode: 'forwards',
  },
};

class List extends Component {
  constructor(props){
    super(props)
    this.state = ({
      displayPost: [],
    })
  }
  componentDidMount(){
    this.node.scrollIntoView();
  }

  postSelectedHandler = id => {
    // this.props.history.push({pathname: '/posts/' + id});
    // this.props.history.push("/list/" + id);
    this.setState(()=>({
      displayPost: [...this.state.displayPost, fackPost[id - 1]],
    }))

    console.log('displayPost', this.state.displayPost)
    console.log(fackPost[id - 1])
  };

  render() {
    const { displayPost} = this.state;
    console.log('displayPost', displayPost)
    return (
      <>
        <div ref={node => this.node = node} />
        {/* <h1 style={styles.h1}>這邊大guy會放一些文章 </h1> */}
        <div style={styles.postsWrapper}>
          {displayPost.length === 0 ?
            fackPost.map(post =>(
              <Posts
                key={post.id}
                topic={post.topic}
                content={post.content}
                clicked={() => this.postSelectedHandler(post.id)}
              />)):<FullPost
              topic={displayPost.length === 0 || displayPost.topic}
              content={displayPost.length === 0 || displayPost.content}
            />}
            {/* {<FullPost
              topic={fackPost[0].topic}
              content={fackPost[0].content}
            />} */}
        </div>
      </>
    )
  }
}


export default Radium(List)
