import React, {Component} from 'react';
import Radium from 'radium';
import Story from './Story';

const appear = Radium.keyframes({
  '0%': {opacity: 0,},
  '100%': {opacity: 1},
}, 'pulse');

const styles = {
  storiesWrapper: {
    height: '500%',
    width: '100%',
    padding: '25% 0 0 0',
    display: 'flex',
    justifyContent: 'center',
    flexDirection: 'column',
  },
  h1:{
    animationName: appear,
    animationDuration: '1s',
    animationFillMode: 'forwards',
  },
};

class List extends Component {
  static fackList = [
    {
      id: 1,
      topic:'test',
      content: 'aeqweqe',
    },
    {
      id: 2,
      topic:'test2',
      content: 'aeqweqe',
    },
    {
      id: 3,
      topic:'test3',
      content: 'aeqweqe',
    },
  ]
  componentDidMount(){
    this.node.scrollIntoView();
  }

  render() { 
    return (
      <>
        <div ref={node => this.node = node} />
        {/* <h1 style={styles.h1}>這邊大guy會放一些文章 </h1> */}
        <div style={styles.storiesWrapper}>
          {List.fackList.map(story =>(
            <Story
              key={story.id}
              topic={story.topic}
              content={story.content}
            />))}
        </div>
      </>
    )
  }
}


export default Radium(List)
