import React, {Component} from 'react';
import Radium from 'radium';
import { useState, useEffect, useRef } from "react";
import useTypewriter from "react-typewriter-hook";
// import {APP} from './Typewriter' ;

// import Parantheses from './parantheses.svg';
import LeftParantheses from './left-parantheses.svg';
import RightParantheses from './right-parantheses.svg'


const MagicOcean = [
  "Yo, did you see that?",
  "Fine, I' ll show you again.",
  "事不过三，bye."
];
let index = 0;


const flip = Radium.keyframes({
  '0%': {transform: 'rotateX(0)'},
  '100%': {transform: 'rotateX(180deg)',
           width: 60, },
}, 'pulse');

const styles = {
  fullStack: {
    height: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  left: {
    position: 'relative',
    width: '50vw',
    height: '100%',
    display: 'flex',
    alignItems: 'center',
    flexDirection: 'row-reverse',
  },
  parantheses: {
    width: '100%',
    display: 'flex',
    alignItems: 'center',
  },
  leftParantheses: {
    position: 'absolute',
    right: 200,
    width: 80,
  },
  leftParanthesesActive:{
    right: 400,
    transitionDuration: '1.5s',
    animationName: flip,
    animationDelay: '1.5s',
    animationDuration: '1s',
    animationFillMode: 'forwards',
  },
  unactive: {
    opactiy: 1,
    fontSize: 46,
    position: 'relative',
    left: '20vw',
    width: 100,
  },
  rightParantheses: {
    position: 'absolute',
    right: 100,
    width: 80,
  },
  rightParanthesesActive: {
    animationName: flip,
    animationDelay: '1.5s',
    animationDuration: '1s',
    animationFillMode: 'forwards',
  },
  right: {
    width: '50vw',   
  },
  fullstackTitle: {
    marginTop: 0,
    fontSize: 42,
    fontWeight: 300,
    letterSpacing: 1.618, //黃金比例，這就對ㄖ
    fontFamily: 'brandon-grotesque,Helvetica Neue,Helvetica,Arial',
  },
  fullstackTitleSpan: {
    fontWeight: 600,
    marginLeft: 10,
  },
  fullstackContent: {
    width: 400,
    lineHeight: 1.618, //黃金比例，這就對ㄖ
    letterSpacing: 1,
  }
}

class MainFullstack extends Component {
  constructor(props){
    super(props)
    this.state = {
      active: false
    }
  }

  App() {
    const [magicName, setMagicName] = useState("magic is going to happen");
    const intervalRef = useRef({});
    const name = useTypewriter(magicName);
    useEffect(
      () => {
        intervalRef.current = setInterval(() => {
          // index = index + 1 > 2 ? 0 : ++index + 1;
          index = index > 2 ? 0 : ++index;
          setMagicName(MagicOcean[index]);
        }, 5000);
        return function clear() {
          clearInterval(intervalRef.current);
        };
      },
      [magicName]
    );
    return (
        <p>{name}</p>
    );
  }  

  render() { 
    const {active} = this.state
    // const {skill} = this.props
    return (
      <section id ="mainFullStack" style={styles.fullStack}>
        <div style={styles.left}>
          <div style={ styles.parantheses} onMouseOver={() => this.setState({
            active: true
          })}>
            <img style={ [styles.leftParantheses, active && styles.leftParanthesesActive] } src={ LeftParantheses } alt=""/>
            {/* <p style={styles.unactive}>
            {skill}
            </p> */}
            <img style={ [styles.rightParantheses, active && styles.rightParanthesesActive] } src={ RightParantheses } alt=""/>
          </div> 
        </div>
        <div style={styles.right}>
          <h2 style={styles.fullstackTitle}>FULL-STACK<span style={styles.fullstackTitleSpan}>DEV</span></h2>
          <p style={styles.fullstackContent}>講一些我會的東東，例如react, redux, 串接api(啊雖然還不會後端但先寫全端就對ㄖ）然後再搭配個個性積極、學習力強、擅長團隊合作......描述自己像個優秀青年這樣。啊希望將來可以用英文打比較潮。</p>
        </div>
        {this.App}
      </section>
    )
  }
}


export default Radium(MainFullstack)
