  postsWrapper: {
    animationName: appear,
    animationDuration: '1s',
    animationFillMode: 'forwards',
    height: '500%',
    width: '100%',
    padding: '15% 0 0 0',
    display: 'flex',
    justifyContent: 'center',
    flexDirection: 'column',
  },
  h1:{
    animationName: appear,
    animationDuration: '1s',
    animationFillMode: 'forwards',
  },
};

class List extends Component {
  static fackPost = [
    {
      id: 1,
      topic: '前端好難',
      content: 'TT',
    },
    {
      id: 2,
      topic:'後端也好難',
      content: 'TAT',
    },
    {
      id: 3,
      topic: '學完redux發現有graphql',
      content: 'QAQ',
    },
  ]
  constructor(props){
    super(props)
    this.state = ({
      displayPost: [],
    })
  }
  componentDidMount(){
    this.node.scrollIntoView();
  }

  postSelectedHandler = id => {
    // this.props.history.push({pathname: '/posts/' + id});
    this.props.history.push("/list/" + id);
    this.setState({
      displayPost: [...this.state.displayPost, List.fackPost[id - 1]],
    })
    console.log('displayId', id - 1)
    console.log('displayPost', this.state.displayPost)
    console.log(List.fackPost[id - 1])
  };

  render() {
    const { displayPost} = this.state;
    const { id } = this.props.match.params;
    console.log('id',id);


    let result = () => {
      let display = null;
      if (id) {
        display = displayPost ? (
          <FullPost
            topic={displayPost.topic}
            content={displayPost.content}
          />
        ) : (
            <h1>Not found.</h1>
        );
      } else {
        display = List.FakePost.map((post, id) => (
          <Posts
            key={id}
            topic={post.topic}
            content={post.content}
            clicked={() => this.postSelectedHandler(post.id)}
          />
        ));
      }
    }

    return (
      result()
    )
  }



  // render() {
